body {
  margin: 0;
}

.splash {
  height: 100vh;
  padding-top: 60px;
  margin: auto;
  position: relative;

  background-image: url(img/bg-img.jpg);
  background-size: cover;
  background-attachment: fixed;

  h1 {
    color: $light-font;
    margin-left: 0.5em;
    font-size: 4em;
    font-family: $txt;
    font-weight: $light;
    word-wrap: break-word;
    border-bottom: 1px whitesmoke solid;
    line-height: 2;

    position: absolute;
    top: 40%;
    transform: translateY(-40%);
  }

  .scroll-indicator {
    position: absolute;
    left: 50%;
    font-size: 3em;

    -webkit-animation: bounce 1.4s infinite;
    animation: bounce 1.4s infinite;
  }

  @-webkit-keyframes bounce {
    0% {
      top: 80%;
    }
    50% {
      top: 83%;
    }
    100% {
      top: 80%;
    }
  }

  @keyframes bounce {
    0% {
      top: 80%;
    }
    50% {
      top: 83%;
    }
    100% {
      top: 80%;
    }
  }

  .fa,
  .fas {
    color: white;
  }
}

nav {
  position: fixed;
  width: 100%;
  z-index: 2;
  text-align: right;
  padding: 1em;
  font-family: $txt;
  font-weight: $light;

  .nav-colored {
    background: $nav-dark;
  }

  .nav-transparent {
    background: transparent;
  }

  a {
    color: $light-font;
    text-decoration: none;
    margin: 2em;
    transform: scaleX(0);
    transition: transform 250ms ease-in-out;
    position: relative;

    &::after {
      position: absolute;
      content: "";
      transition: width 0.3s;
      display: inline-block;
      bottom: 0;
      left: 0;
      width: 0;
    }

    &:hover::after {
      width: 100%;
      transform: scaleX(1);
      border-bottom: 1px $light-font solid;
    }
  }
}

section {
  position: relative;
  h1 {
    font-family: $txt;
    font-weight: $light;
    font-size: 2.5em;
    text-align: center;
  }

  h4 {
    font-size: 10em;
    opacity: 0.1;
    font-family: $txt;
    position: absolute;
    margin: 0;
    top: -0.2em;
    left: 0.3em;
    color: $dark-font;
  }

  p {
    font-family: $txt;
    font-weight: $regular;
  }
}

#info {
  max-width: 60vw;
  margin: 0 auto;
  padding: 4em;
}

#portfolio {
  border-top: 1px grey solid;
  margin: 1.5em;
  align-items: center;

  .portfolio {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    margin: 5em 1em 0em 1em;

    img {
      max-height: 550px;
      transition: all 0.5s ease-in-out;

      &:hover {
        transform: scale(1.03, 1.03);
      }
    }

    p {
      padding-right: 3em;
      margin-top: 0;
    }
  }
}

#contact {
  margin: 4em 1.5em;
  border-top: 1px grey solid;

  .contact-txt {
    max-width: 60vw;
    margin: 0 auto;
  }

  .contact-info {
    max-width: 60vw;
    margin: 0 auto;
    padding-top: 5em;
  }

  .tlf:before {
    font-family: "Material Icons";
    content: "\e325";
    font-size: 2em;
    padding-right: 0.5em;
  }
  .mail:before {
    font-family: "Material Icons";
    content: "\e0be";
    font-size: 2em;
    padding-right: 0.5em;
  }
  .adress:before {
    font-family: "Material Icons";
    content: "\e88a";
    font-size: 2em;
    padding-right: 0.5em;
  }
